import React, { useEffect, useState } from 'react';
import axios from 'axios';
import ContactForm from './components/ContactForm';
import ContactList from './components/ContactList';
import './styles/App.css';

function App() {
    const [contacts, setContacts] = useState([]);

    // Fetch contacts when the component mounts
    useEffect(() => {
        fetchContacts();
    }, []);

    // GET request to retrieve contacts
    const fetchContacts = async () => {
        try {
            const response = await axios.get('http://localhost:5001/api/contacts'); // Adjust to the backend server URL
            setContacts(response.data);
        } catch (error) {
            console.error('Error fetching contacts:', error);
        }
    };

    // Adding a new contact and updating state
    const addContact = (newContact) => {
        setContacts([...contacts, newContact]);
    };

    // Deleting a contact and updating state
    const deleteContact = (id) => {
        setContacts(contacts.filter(contact => contact._id !== id));
    };

    // Updating an existing contact and state
    const updateContact = (id, updatedContact) => {
        setContacts(contacts.map(contact => (contact._id === id ? updatedContact : contact)));
    };

    return (
        <div className="App">
            <h1>ðŸ“‡ Phone Book</h1>
            <ContactForm addContact={addContact} />
            <ContactList
                contacts={contacts}
                deleteContact={deleteContact}
                updateContact={updateContact}
            />
        </div>
    );
}

export default App;
