import React, { useState } from 'react';
import axios from 'axios';

const ContactForm = ({ addContact }) => {
    const [name, setName] = useState('');
    const [phone, setPhone] = useState('');
    const [email, setEmail] = useState('');
    const [error, setError] = useState('');
    const [success, setSuccess] = useState('');

    const handleSubmit = async (event) => {
        event.preventDefault();
        if (!name || !phone || !email) {
            setError('All fields are required.');
            return;
        }

        const contactData = { name, phone, email };

        try {
            const response = await axios.post('http://localhost:8500/api/contacts', contactData);
            console.log('Contact added successfully:', response.data);
            setSuccess('Contact added successfully!'); // Set success message
            setError(''); // Clear any previous error
        } catch (error) {
            console.error('Error adding contact:', error.response ? error.response.data : error.message);
            setError('Failed to add contact. Please try again.'); // Set error message
        }
    };

    return (
        <div>
            <h2>Add Contact</h2>
            <form onSubmit={handleSubmit}>
                <div>
                    <label>Name:</label>
                    <input
                        type="text"
                        value={name}
                        onChange={(e) => setName(e.target.value)}
                        required
                    />
                </div>
                <div>
                    <label>Phone:</label>
                    <input
                        type="text"
                        value={phone}
                        onChange={(e) => setPhone(e.target.value)}
                        required
                    />
                </div>
                <div>
                    <label>Email:</label>
                    <input
                        type="email"
                        value={email}
                        onChange={(e) => setEmail(e.target.value)}
                        required
                    />
                </div>
                <button type="submit">Add Contact</button>

                {/* Success and error messages */}
                {success && <p>{success}</p>}
                {error && <p style={{ color: 'red' }}>{error}</p>} {/* Display error message */}
            </form>
        </div>
    );
};

export default ContactForm;
