import React, { useState } from 'react';
import axios from 'axios';

const ContactForm = ({ addContact }) => {
    const [name, setName] = useState('');
    const [phone, setPhone] = useState('');
    const [email, setEmail] = useState('');
    const [error, setError] = useState('');
    const [success, setSuccess] = useState('');

    const handleSubmit = async (event) => {
        event.preventDefault();
        if (!name || !phone || !email) {
            setError('All fields are required.');
            return;
        }

        const contactData = { name, phone, email };

        try {
            const response = await axios.post('http://localhost:3000/api/contacts', contactData);
            addContact(response.data);
            setSuccess('Contact added successfully!');
            setError('');
            setName('');  // Reset the input fields after successful submission
            setPhone('');
            setEmail('');
        } catch (error) {
            if (error.response) {
                console.error('Error response data:', error.response.data);
                console.error('Error response status:', error.response.status);
                console.error('Error response headers:', error.response.headers);
                setError(`Failed to add contact: ${error.response.data.message || 'Please try again.'}`);
            } else if (error.request) {
                console.error('Error request:', error.request);
                setError('No response from server. Please try again later.');
            } else {
                console.error('Error message:', error.message);
                setError('An error occurred. Please try again.');
            }
        }
    };

    return (
        <div>
            <h2>Add Contact</h2>
            <form onSubmit={handleSubmit}>
                <div>
                    <label>Name:</label>
                    <input
                        type="text"
                        value={name}
                        onChange={(e) => setName(e.target.value)}
                        required
                    />
                </div>
                <div>
                    <label>Phone:</label>
                    <input
                        type="text"
                        value={phone}
                        onChange={(e) => setPhone(e.target.value)}
                        required
                    />
                </div>
                <div>
                    <label>Email:</label>
                    <input
                        type="email"
                        value={email}
                        onChange={(e) => setEmail(e.target.value)}
                        required
                    />
                </div>
                <button type="submit">Add Contact</button>

                {/* Success and error messages */}
                {success && <p style={{ color: 'green' }}>{success}</p>}
                {error && <p style={{ color: 'red' }}>{error}</p>}
            </form>
        </div>
    );
};

export default ContactForm;
