import React, { useState } from 'react';
import axios from 'axios';

function ContactForm({ addContact }) {
    const [name, setName] = useState('');
    const [phoneNumber, setPhoneNumber] = useState('');
    const [email, setEmail] = useState('');

    const handleSubmit = async (e) => {
        e.preventDefault();
        const newContact = { name, phone: phoneNumber, email }; // Ensure phone key is 'phone' as expected by backend
        try {
            const response = await axios.post('http://localhost:3000/api/contacts', newContact);  // POST request
            addContact(response.data);
            setName('');
            setPhoneNumber('');
            setEmail('');
        } catch (error) {
            console.error('Error adding contact:', error);
            alert('Failed to add contact. Please try again.'); // Added user alert for error feedback
        }
    };

    return (
        <form onSubmit={handleSubmit}>
            <input
                type="text"
                value={name}
                onChange={(e) => setName(e.target.value)}
                placeholder="Name"
                required
            />
            <input
                type="text"
                value={phoneNumber}
                onChange={(e) => setPhoneNumber(e.target.value)}
                placeholder="Phone Number"
                required
            />
            <input
                type="email"
                value={email}
                onChange={(e) => setEmail(e.target.value)}
                placeholder="Email"
            />
            <button type="submit">Add Contact</button>
        </form>
    );
}

export default ContactForm;
